import {Button, Header} from 'semantic-ui-react'
import {useState} from 'react'


function App() {
  const [gen, setGen] = useState("Hi")

  function generateCoffee() {
    setGen(`${gen}+hi`)
  }
  return (
    <div className='ctr'>
      {gen ? 
      <Header as='h1' inverted>{gen}</Header> 
      : null}
      <Button size="massive" fluid='false' inverted icon='coffee' content="Generate Coffee" onClick={generateCoffee}/>
    </div>
  );
}

export default App;
